package com.sparrowwallet.drongo.protocol;

import com.sparrowwallet.drongo.Network;
import com.sparrowwallet.drongo.Utils;
import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

public class BlockTest {
    @Test
    public void testBlock() {
        Network.set(Network.TESTNET);

        String hex = "";
        Block block = new Block(Utils.hexToBytes(hex));
        Assertions.assertEquals("000000000ee6e33bd782d55877515b2cfa6aaacd0774a5c8bc357252955f3795", block.getHash().toString());
        Assertions.assertEquals(1, block.getTransactions().size());
    }

    @Test
    public void testLargeBlock() {
        Network.set(Network.TESTNET);

        String hex = "";
        Block block = new Block(Utils.hexToBytes(hex));
        Assertions.assertEquals("00000000009e9f0f2585aec1b003156e74d15c5e407bd1450d937a7b6c1f4fe4", block.getHash().toString());
        Assertions.assertEquals(40, block.getTransactions().size());
        Assertions.assertEquals(Sha256Hash.wrap("1d3191e3447a6725d8a5be53cc9a931f30d95d649c202f7c8272793ee6357c60"), block.getTransactions().get(1).getTxId());
        Assertions.assertEquals(Sha256Hash.wrap("dfb588d52eab6091649b77fdcd5f41e8eeec645519032b6c55fff3f5308715d3"), block.getTransactions().get(39).getTxId());
    }

    @AfterEach
    public void tearDown() throws Exception {
        Network.set(null);
    }
}
